		  'SOF-EPC' => 'Sofosbuvir (a component of Epclusa' + tm + ' and Vosevi' + tm + ')',
		  'DAS' => 'Dasabuvir (a component of Holkira Pak' + tm + ')',
		  'ASV' => 'Asunaprevir (Sunvepra' + tm + ')',
		  'GLP' => 'Glecaprevir (a component of Maviret' + tm + ')',
		  'VOX' => 'Voxilaprevir (a component of Vosevi' + tm + ')',
