			['NS5B', '4'] => '',
			['NS5B', '5'] => '',
			['NS5B', '6'] => '',
			['NS5B', '6E'] => '',
		}

		@drug_lists = {'NS3' => [],'NS5A' => [],'NS5B' => [],}
		@drug_lists.default = []

		@yaml = nil
		File.open(filename) do |file|
		  @yaml = YAML.load(file.gets(nil))
		end

		#might actually replace this with hard codes drugs/pretty names for report.
		@yaml.each do |drug|
			@drug_lists[drug['genotypes'].first()['region'].upcase()] << drug['code']
		end
	end #end initialize

	#returns a result
	def interpret(genotype, region, aa_seq)
		result = {
			:version => @version,
			:version_url => @version_url,
			:region => region.upcase(),
			:genotype => genotype.upcase(),
			:drugs => [],
			:mutations => [], #how?
		}

		@drug_lists[region.upcase()].each do |drug_code|
			drug = @yaml.find(){|a| a['code'] == drug_code}
			gt = drug['genotypes'].find(){|a| a['genotype'].upcase() == genotype.upcase()}
			rules = nil

			drug_res = {:code => drug['code'], :name => drug['name'],
					:score => nil, :level => nil, :interp => nil}

			drug_res[:nice_name] = @nice_names[drug['code']]
			drug_res[:nice_name] = drug['name'] if(drug_res[:nice_name] == nil)

			if(!gt)
				drug_res[:score] = 0
				drug_res[:level] = -1
			else
				rules = gt['rules']

				x = @alg.interp_condition(rules, aa_seq)

				drug_res[:score] = x[1]
				flags = x[2]

				if(drug_res[:score] == 0 and !flags.empty?()) #use flags
					if(flags.include?('Not available'))
						drug_res[:level] = -1
					elsif(flags.include?('Not indicated'))
						drug_res[:level] = 2
					elsif(flags.include?('Effect unknown'))
						drug_res[:level] = 3
					end
				else
					@global_range.each do |rng|
						drug_res[:level] = rng[2] if(drug_res[:score] >= rng[0] and drug_res[:score] <= rng[1])
					end
				end

				#still need to get mutations.  Lets be lazy, this should work.
				rules.scan(/([A-Za-z\-])(\d+)([A-Za-z\-id!]+)\s*=>/) do |wild, loc, aa|
					if(aa_seq[loc.to_i] == [])
						drug_res[:level] = 0
						drug_res[:score] = 0
					elsif(aa_seq[loc.to_i - 1] and aa_seq[loc.to_i - 1] != [wild])
						result[:mutations] << [wild, loc.to_i, aa_seq[loc.to_i - 1]]
					end
				end

				#I think we need to set the "Missing coverage" stuff based on the rules scan rule you have per drug.

			end #end !gt

			drug_res[:interp] = @level_def[drug_res[:level]]
			result[:drugs] << drug_res

		end	 #end @drug_lists[region.upcase()].each
		result[:mutations] = result[:mutations].uniq().sort(){|a,b| a[1] <=> b[1] }
		return result
	end #end interpet

end


